			<!-- Right Section (Login Form) -->
			<div class="flex justify-center items-center w-full md:w-1/2 p-4 md:p-0">
				<div class="w-full max-w-md p-8 bg-white rounded-lg shadow-lg">
					<!-- Title -->
					<h2 class="text-2xl md:text-3xl mt-10 font-bold text-center">Welcome back!</h2>
					<div class="text-gray-500 mb-20 text-center">Sign in</div>
					<!-- Login Form -->
					<form @submit.prevent="login">
						<!-- E-mail Field -->
						<div class="mb-4">
							<label for="email" class="block text-sm font-medium">E-mail</label>
							<input
								v-model="form.email"
								id="email"
								type="email"
								placeholder="E-mail"
								class="w-full px-4 py-2 border rounded-lg"
							/>
						</div>
						<!-- Password Field -->
						<div class="mb-4">
							<label for="password" class="block text-sm font-medium">Password</label>
							<div class="relative">
								<input
									v-model="form.password"
									id="password"
									type="password"
									placeholder="Password"
									class="w-full px-4 py-2 border rounded-lg"
								/>
							</div>
						</div>
						<!-- Sign In Button -->
						<button
							type="submit"
							class="w-full py-2 px-4 bg-gray-600 text-white rounded-lg hover:bg-gray-700"
						>
							Sign in
						</button>
						<!-- Forgot Password Link -->
						<div class="text-center mt-4">
							<router-link to="/password/forget" class="text-gray-600">Forgot your password?</router-link>
